import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for stored user data on app load
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = async (email, password, userType) => {
    try {
      // Admin authentication - fetch real admin data from API
      if (userType === 'admin') {
        try {
          const response = await fetch('http://localhost:5000/api/admins/login', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email }),
          });

          if (response.ok) {
            const data = await response.json();
            // Simple password validation (in real app, this would be done on backend)
            if (password === 'admin123') {
              const userData = {
                ...data.admin,
                type: 'admin',
                college: data.college
              };
              setUser(userData);
              localStorage.setItem('user', JSON.stringify(userData));
              return { success: true, user: userData };
            } else {
              return { success: false, error: 'Invalid password' };
            }
          } else {
            return { success: false, error: 'Admin not found' };
          }
        } catch (apiError) {
          return { success: false, error: 'Failed to verify admin credentials' };
        }
      }

      // Student authentication - fetch real student data from API
      if (userType === 'student') {
        try {
          const response = await fetch(`http://localhost:5000/api/students?email=${encodeURIComponent(email)}`);
          if (response.ok) {
            const studentData = await response.json();
            // Simple password validation (in real app, this would be done on backend)
            if (password === 'student123') {
              const userData = {
                ...studentData,
                type: 'student'
              };
              setUser(userData);
              localStorage.setItem('user', JSON.stringify(userData));
              return { success: true, user: userData };
            } else {
              return { success: false, error: 'Invalid password' };
            }
          } else {
            // If specific student not found, try to get any student for demo purposes
            if (email === 'student@campus.edu') {
              try {
                const allStudentsResponse = await fetch('http://localhost:5000/api/students');
                if (allStudentsResponse.ok) {
                  const allStudents = await allStudentsResponse.json();
                  if (allStudents.length > 0 && password === 'student123') {
                    const firstStudent = allStudents[0];
                    const userData = {
                      ...firstStudent,
                      type: 'student',
                      email: 'student@campus.edu', // Keep the login email
                      name: 'Demo Student'
                    };
                    setUser(userData);
                    localStorage.setItem('user', JSON.stringify(userData));
                    return { success: true, user: userData };
                  }
                }
              } catch (fallbackError) {
                console.error('Fallback authentication failed:', fallbackError);
              }
            }
            return { success: false, error: 'Student not found' };
          }
        } catch (apiError) {
          return { success: false, error: 'Failed to verify student credentials' };
        }
      }

      return { success: false, error: 'Invalid credentials' };
    } catch (error) {
      return { success: false, error: 'Login failed' };
    }
  };

  const registerStudent = async (studentData) => {
    try {
      const response = await fetch('http://localhost:5000/api/students/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(studentData),
      });

      const data = await response.json();

      if (response.ok) {
        return { success: true, student: data.student };
      } else {
        return { success: false, error: data.error };
      }
    } catch (error) {
      return { success: false, error: 'Registration failed' };
    }
  };

  const registerAdmin = async (adminData) => {
    try {
      const response = await fetch('http://localhost:5000/api/admins/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(adminData),
      });

      const data = await response.json();

      if (response.ok) {
        return { success: true, admin: data.admin };
      } else {
        return { success: false, error: data.error };
      }
    } catch (error) {
      return { success: false, error: 'Registration failed' };
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const value = {
    user,
    login,
    logout,
    registerStudent,
    registerAdmin,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
